
public class rectanglestar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=5;
		for(int i=n;i>0;i--)
		
		{
			for(int j=1;j<i;j++)
			{
				System.out.print(" ");
			
				
				
				
			}
			for(int k=5 ; k>4; k--)
			{
			
			System.out.print("*");
			}
			System.out.print("\n");
		}
		
		
	}

}
